"use strict";

var sub = require("./sub.js");
var subCached = require("./subCached.js");
var subCircular = require("./subCircular.js");

setTimeout(function() {
  var delayed = require("./delayed.js");
  console.info("Hello from main! "+delayed.greet());
}, 100);

exports.greet = function() {
  return "Hello from main! "+sub.greet() +
    " " + subCached.greet() +
    " " + subCircular.greet();
};

exports.pathsRelative = function() {
  return require.resolve.paths("./sub.js");
};

exports.pathsGlobal = function() {
  return require.resolve.paths("module1.js");
};

exports.pathsAbsolute = function() {
  return require.resolve.paths("/module1.js");
};
